scripts.production:i18n:
ELEVENTY_LOCALES=(en de) 
 for locale in ${ELEVENTY_LOCALES[@]} 
 do 
 rm -rf ./dist-i18n/${locale} ; ELEVENTY_LOCALES=${ELEVENTY_LOCALES[@]} ELEVENTY_LOCALE=${locale} npm run production && mkdir -p dist-i18n/${locale} && cp -R dist/ dist-i18n/${locale} 
 done && rm -rf ./dist && npm run clean:cache

scripts.production:pwa:i18n:
ELEVENTY_LOCALES=(en de) 
 for locale in ${ELEVENTY_LOCALES[@]} 
 do 
 rm -rf ./dist-i18n/${locale} ; ELEVENTY_LOCALES=${ELEVENTY_LOCALES[@]} ELEVENTY_LOCALE=${locale} npm run production:pwa && mkdir -p dist-i18n/${locale} && cp -R dist/ dist-i18n/${locale} 
 done && rm -rf ./dist && npm run clean:cache
